import React from "react";
import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter
} from "reactstrap";
import "./Contact.css";
import Upload from "./UploadFile.jsx";
import axios from "axios";
import { server } from "../../conf";

class Contact extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false
    };

    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }
  // submit() {
  //   const { pseudo, email, message } = this.state;
  //   if (pseudo !== "" && email !== "" && message !== "") {
  //     if (this.state.email.includes("@")) {
  //       axios.post(`${server}contact`, {

  //       });
  //       this.setState({
  //         pseudo: "",
  //         email: "",
  //         message: ""
  //       });
  //       this.toggle();
  //       this.toggle2();
  //     } else {
  //       this.EmptyFieldMail();
  //     }
  //   } else {
  //     this.EmptyField();
  //   }
  // }
  render() {
    return (
      <div>
        <div className="contactButton" onClick={() => this.toggle()}>
          Contact Us
        </div>
        <Modal
          isOpen={this.state.modal}
          toggle={this.toggle}
          className={this.props.className}
        >
          <ModalHeader toggle={this.toggle}>Contact Us</ModalHeader>
          <ModalBody>
            <Form>
              <FormGroup>
                <Label for="exampleEmail">Email</Label>
                <Input
                  type="email"
                  name="email"
                  id="exampleEmail"
                  placeholder="put your email here"
                />
              </FormGroup>
              <FormGroup>
                <Label for="exampleSelect">Select</Label>
                <Input type="select" name="select" id="exampleSelect">
                  <option>Why do you want to contact us ?</option>
                  <option>I want a payback</option>
                  <option>I had a problem with the Circus</option>
                  <option>I've lost something</option>
                  <option>Else</option>
                </Input>
              </FormGroup>

              <FormGroup>
                <Label for="exampleText">You can explain us everything</Label>
                <Input
                  type="textarea"
                  name="text"
                  id="exampleText"
                  placeholder="Explain everything here"
                />
              </FormGroup>
              <FormGroup>
                <Upload />
              </FormGroup>
            </Form>
          </ModalBody>
          <ModalFooter>
            <Button onClick={() => this.submit()}>Send</Button>
            <Button onClick={this.toggle}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default Contact;
